package week1.day2.assignements;

public class CheckthenumberisPositiveorNegative {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int number = 35;
		if(number>0){
		System.out.println("The Given number is Positive :" +number);
	
		
		}
		else if(number<0) {
			System.out.println(" The Given Number is negative : " +number);
			
		}
		
		else if(number==0)	{
			System.out.println(" The Given Number is Neither positive or Negative :" +number);
		}
			
		
			
		
	}
	
}
		
	


